mapper.select:
        SELECT
        	COUNT(*) AS COUNT
        FROM
        	(
        		SELECT
        			C.STATUS AS READ_STATUS
        		FROM
        			IMB_ARTICLE A LEFT OUTER JOIN IMB_ARTICLE_READER C ON A.BOARD_ID = C.BOARD_ID AND A.SEQ = C.ARTICLE_SEQ AND C.USER_ID = #{signinUserId}
        			LEFT OUTER JOIN IMB_BOARD_AUTH D ON A.BOARD_ID = D.BOARD_ID
        			LEFT OUTER JOIN IMB_USER B ON A.WRITER_ID = B.ID
        		WHERE
        				(IS_NOTICE IS NULL OR IS_NOTICE = 'N')
        			AND (D.VIEW_LIST_LEVEL IS NULL OR D.VIEW_LIST_LEVEL >= #{signinUserLevel})
        			AND (A.STATUS = 1 OR A.WRITER_ID = #{signinUserId})

        				AND A.BOARD_ID = #{boardId}


			            AND SUBJECT LIKE CONCAT('%',#{subject},'%')


			            AND CONTENT LIKE CONCAT('%',#{content},'%')


			            AND WRITER_ID = #{writerId}


			            AND WRITER_ID LIKE CONCAT('%',#{likeWriterId},'%')


			            AND WRITER_NAME = #{writerName}


			            AND WRITER_NAME LIKE CONCAT('%',#{likeWriterName},'%')


			            AND TAG LIKE CONCAT('%', #{tag}, '%')


			        	AND A.STATUS = #{status}


			        	AND C.STATUS = #{readStatus}


			        	AND B.DISPLAY_ID = #{writerDisplayId}


			        	AND B.DISPLAY_ID LIKE CONCAT('%', #{likeWriterDisplayId}, '%')


			            AND DATE_FORMAT(A.REGISTER_DATE, '%Y-%m-%d %H:%i:%s') = #{registerDate} 


						AND THUMBNAIL_URL IS NOT NULL    
						AND THUMBNAIL_URL != ''        

			        ORDER BY
			        	GROUP_ID ${orderByGroupId}DESC
			       		,SEQ ${orderBySeq}DESC
			       		, IS_NOTICE ${orderByIsNotice}
			       		, REGISTER_DATE ${orderByRegisterDate}
        	) B

       		LIMIT #{startIndex}, #{endIndex}

SELECT * FROM (
(SELECT A.BOARD_ID, (SELECT NAME FROM IMB_BOARD WHERE ID = A.BOARD_ID) AS BOARD_NAME, A.SEQ, A.GROUP_ID, A.SUBJECT,
A.CONTENT,
'' AS CONTENT,
DATE_FORMAT(A.REGISTER_DATE, '%Y-%m-%d %H:%i:%s') AS REGISTER_DATE, A.HIT, A.GOOD, A.BAD, A.WRITER_ID, A.WRITER_NAME, B.DISPLAY_ID AS WRITER_DISPLAY_ID, B.PROFILE_IMG_URL AS WRITER_PROFILE_IMG_URL, A.STATUS, A.THUMBNAIL_URL, A.TAG, A.IS_NOTICE, (SELECT COUNT(SEQ) FROM IMB_COMMENT WHERE A.BOARD_ID = BOARD_ID AND A.SEQ = ARTICLE_SEQ) AS COMMENT_COUNT, '' AS PASSWORD, C.STATUS AS READ_STATUS FROM IMB_ARTICLE AS A LEFT OUTER JOIN IMB_USER B ON A.WRITER_ID = B.ID LEFT OUTER JOIN IMB_ARTICLE_READER C ON C.BOARD_ID = A.BOARD_ID AND C.ARTICLE_SEQ = A.SEQ AND C.USER_ID = #{signinUserId} LEFT OUTER JOIN IMB_BOARD_AUTH D ON A.BOARD_ID = D.BOARD_ID WHERE A.IS_NOTICE = 'Y' AND (D.VIEW_LIST_LEVEL IS NULL OR D.VIEW_LIST_LEVEL >= #{signinUserLevel}) AND (A.STATUS = 1 OR A.WRITER_ID = #{signinUserId} OR #{signinUserLevel} < 0)
AND A.BOARD_ID = #{boardId}
)
UNION
( SELECT A.BOARD_ID, (SELECT NAME FROM IMB_BOARD WHERE ID = A.BOARD_ID) AS BOARD_NAME, A.SEQ, A.GROUP_ID, A.SUBJECT,
A.CONTENT,
'' AS CONTENT,
DATE_FORMAT(A.REGISTER_DATE, '%Y-%m-%d %H:%i:%s') AS REGISTER_DATE, A.HIT, A.GOOD, A.BAD, A.WRITER_ID, A.WRITER_NAME, B.DISPLAY_ID AS DISPLAY_ID, B.PROFILE_IMG_URL AS WRITER_PROFILE_IMG_URL, A.STATUS, A.THUMBNAIL_URL, A.TAG, A.IS_NOTICE, (SELECT COUNT(SEQ) FROM IMB_COMMENT WHERE A.BOARD_ID = BOARD_ID AND A.SEQ = ARTICLE_SEQ) AS COMMENT_COUNT, A.PASSWORD, C.STATUS AS READ_STATUS FROM IMB_ARTICLE AS A LEFT OUTER JOIN IMB_USER B ON A.WRITER_ID = B.ID LEFT OUTER JOIN IMB_ARTICLE_READER C ON C.BOARD_ID = A.BOARD_ID AND C.ARTICLE_SEQ = A.SEQ AND C.USER_ID = #{signinUserId} LEFT OUTER JOIN IMB_BOARD_AUTH D ON A.BOARD_ID = D.BOARD_ID WHERE (IS_NOTICE IS NULL OR IS_NOTICE = 'N') AND (D.VIEW_LIST_LEVEL IS NULL OR D.VIEW_LIST_LEVEL >= #{signinUserLevel}) AND (A.STATUS = 1 OR A.WRITER_ID = #{signinUserId} OR #{signinUserLevel} < 0)
AND A.BOARD_ID = #{boardId}
AND SUBJECT LIKE CONCAT('%',#{subject},'%')
AND CONTENT LIKE CONCAT('%',#{content},'%')
AND WRITER_ID = #{writerId}
AND WRITER_ID LIKE CONCAT('%',#{likeWriterId},'%')
AND WRITER_NAME = #{writerName}
AND WRITER_NAME LIKE CONCAT('%',#{likeWriterName},'%')
AND TAG LIKE CONCAT('%', #{tag}, '%')
AND A.STATUS = #{status}
AND C.STATUS = #{readStatus}
AND B.DISPLAY_ID LIKE CONCAT('%', #{likeWriterDisplayId}, '%')
AND B.DISPLAY_ID = #{writerDisplayId}
AND DATE_FORMAT(REGISTER_DATE, '%Y-%m-%d') = #{registerDate}
AND THUMBNAIL_URL IS NOT NULL AND THUMBNAIL_URL != ''
ORDER BY GROUP_ID ${orderByGroupId}DESC ,SEQ ${orderBySeq}DESC , REGISTER_DATE ${orderByRegisterDate}
LIMIT #{startIndex}, #{endIndex}
)
) ARTICLE ORDER BY IS_NOTICE ${orderByIsNotice}IS_NOTICE DESC ,GROUP_ID ${orderByGroupId}DESC ,SEQ ${orderBySeq}DESC , REGISTER_DATE ${orderByRegisterDate}

SELECT A.BOARD_ID, (SELECT NAME FROM IMB_BOARD WHERE ID = A.BOARD_ID) AS BOARD_NAME, SEQ, SUBJECT, CONTENT, DATE_FORMAT(A.REGISTER_DATE, '%Y-%m-%d %H:%i:%s') AS REGISTER_DATE, HIT, GOOD, BAD, A.WRITER_ID, A.WRITER_NAME, B.DISPLAY_ID AS DISPLAY_ID, B.PROFILE_IMG_URL AS WRITER_PROFILE_IMG_URL, GROUP_ID, STATUS, THUMBNAIL_URL, TAG, IS_NOTICE, (SELECT COUNT(SEQ) FROM IMB_COMMENT WHERE A.BOARD_ID = BOARD_ID AND A.SEQ = ARTICLE_SEQ) AS COMMENT_COUNT, A.PASSWORD, (SELECT STATUS FROM IMB_ARTICLE_READER WHERE BOARD_ID = A.BOARD_ID AND ARTICLE_SEQ = A.SEQ AND USER_ID = #{signinUserId}) AS READ_STATUS FROM IMB_ARTICLE AS A LEFT OUTER JOIN IMB_USER B ON A.WRITER_ID = B.ID LEFT OUTER JOIN IMB_BOARD_AUTH D ON D.BOARD_ID = A.BOARD_ID WHERE (D.VIEW_DETAIL_LEVEL IS NULL OR D.VIEW_DETAIL_LEVEL >= #{signinUserLevel}) AND (A.STATUS = 1 OR A.WRITER_ID = #{signinUserId} OR #{signinUserLevel} < 0) AND A.BOARD_ID = #{boardId} AND SEQ = #{seq}
AND SUBJECT LIKE CONCAT('%',#{subject},'%')
AND CONTENT LIKE CONCAT('%',#{content},'%')
AND WRITER_ID = #{writerId}
AND WRITER_ID LIKE CONCAT('%',#{likeWriterId},'%')
AND WRITER_NAME = #{writerName}
AND WRITER_NAME LIKE CONCAT('%',#{likeWriterName},'%')
AND TAG LIKE CONCAT('%', #{tag}, '%')
AND A.STATUS = #{status}
AND C.STATUS = #{readStatus}
AND B.DISPLAY_ID LIKE CONCAT('%', #{likeWriterDisplayId}, '%')
AND B.DISPLAY_ID = #{writerDisplayId}
AND IS_NOTICE = #{isNotice}
AND DATE_FORMAT(REGISTER_DATE, '%Y-%m-%d') = #{registerDate}
AND THUMBNAIL_URL IS NOT NULL AND THUMBNAIL_URL != ''

mapper.update:
UPDATE IMB_ARTICLE SET
	HIT = HIT + 1
WHERE
		BOARD_ID = #{boardId}
	AND SEQ = #{seq}

UPDATE IMB_ARTICLE SET


			GOOD = GOOD ${good}


			GOOD = GOOD + 1


WHERE
		BOARD_ID = #{boardId}
	AND SEQ = #{seq}

UPDATE IMB_ARTICLE SET


			BAD = BAD ${bad}


			BAD = BAD + 1


WHERE
		BOARD_ID = #{boardId}
	AND SEQ = #{seq}

UPDATE IMB_ARTICLE SET
	STATUS = #{status}
WHERE
		BOARD_ID = #{boardId}
	AND SEQ = #{seq}

mapper.delete:
DELETE FROM
	IMB_ARTICLE
WHERE
		BOARD_ID = #{boardId}
	AND SEQ = #{seq}
