mapper.select:
        SELECT
        	COUNT(*) AS COUNT
        FROM
        	(
        		SELECT
        			C.STATUS AS READ_STATUS
        		FROM
        			IMB_ARTICLE A LEFT OUTER JOIN IMB_ARTICLE_READER C ON A.BOARD_ID = C.BOARD_ID AND A.SEQ = C.ARTICLE_SEQ AND C.USER_ID = #{signinUserId}
        			LEFT OUTER JOIN IMB_BOARD_AUTH D ON A.BOARD_ID = D.BOARD_ID
        			LEFT OUTER JOIN IMB_USER B ON A.WRITER_ID = B.ID
        		WHERE
        				(IS_NOTICE IS NULL OR IS_NOTICE = 'N')
        			AND (D.VIEW_LIST_LEVEL IS NULL OR D.VIEW_LIST_LEVEL >= #{signinUserLevel})
        			AND (A.STATUS = 1 OR A.WRITER_ID = #{signinUserId})

        				AND A.BOARD_ID = #{boardId}


			            AND SUBJECT LIKE CONCAT('%',#{subject},'%')


			            AND CONTENT LIKE CONCAT('%',#{content},'%')


			            AND WRITER_ID = #{writerId}


			            AND WRITER_ID LIKE CONCAT('%',#{likeWriterId},'%')


			            AND WRITER_NAME = #{writerName}


			            AND WRITER_NAME LIKE CONCAT('%',#{likeWriterName},'%')


			            AND TAG LIKE CONCAT('%', #{tag}, '%')


			        	AND A.STATUS = #{status}


			        	AND C.STATUS = #{readStatus}


			        	AND B.DISPLAY_ID = #{writerDisplayId}


			        	AND B.DISPLAY_ID LIKE CONCAT('%', #{likeWriterDisplayId}, '%')


			            AND DATE_FORMAT(A.REGISTER_DATE, '%Y-%m-%d %H:%i:%s') = #{registerDate} 


						AND THUMBNAIL_URL IS NOT NULL    
						AND THUMBNAIL_URL != ''        

			        ORDER BY
			        	A.REGISTER_DATE DESC , A.GROUP_ID DESC , A.SEQ DESC
        	) B


        		LIMIT #{startIndex}, #{endIndex}

SELECT * FROM (
(SELECT A.BOARD_ID, (SELECT NAME FROM IMB_BOARD WHERE ID = A.BOARD_ID) AS BOARD_NAME, A.SEQ, A.GROUP_ID, A.SUBJECT,
A.CONTENT,
'' AS CONTENT,
DATE_FORMAT(A.REGISTER_DATE, '%Y-%m-%d %H:%i:%s') AS REGISTER_DATE, A.HIT, A.GOOD, A.BAD, A.WRITER_ID, A.WRITER_NAME, B.DISPLAY_ID AS WRITER_DISPLAY_ID, B.PROFILE_IMG_URL AS PROFILE_IMG_URL, A.STATUS, A.THUMBNAIL_URL, A.TAG, A.IS_NOTICE, (SELECT COUNT(SEQ) FROM IMB_COMMENT WHERE A.BOARD_ID = BOARD_ID AND A.SEQ = ARTICLE_SEQ) AS COMMENT_COUNT, '' AS PASSWORD, C.STATUS AS READ_STATUS FROM IMB_ARTICLE AS A LEFT OUTER JOIN IMB_USER B ON A.WRITER_ID = B.ID LEFT OUTER JOIN IMB_ARTICLE_READER C ON C.BOARD_ID = A.BOARD_ID AND C.ARTICLE_SEQ = A.SEQ AND C.USER_ID = #{signinUserId} LEFT OUTER JOIN IMB_BOARD_AUTH D ON A.BOARD_ID = D.BOARD_ID WHERE A.IS_NOTICE = 'Y' AND (D.VIEW_LIST_LEVEL IS NULL OR D.VIEW_LIST_LEVEL >= #{signinUserLevel}) AND (A.STATUS = 1 OR A.WRITER_ID = #{signinUserId} OR #{signinUserLevel} < 0)
AND A.BOARD_ID = #{boardId}
)
UNION
(SELECT A.BOARD_ID, (SELECT NAME FROM IMB_BOARD WHERE ID = A.BOARD_ID) AS BOARD_NAME, A.SEQ, A.GROUP_ID, A.SUBJECT,
A.CONTENT,
'' AS CONTENT,
DATE_FORMAT(A.REGISTER_DATE, '%Y-%m-%d %H:%i:%s') AS REGISTER_DATE, A.HIT, A.GOOD, A.BAD, A.WRITER_ID, A.WRITER_NAME, B.DISPLAY_ID AS DISPLAY_ID, B.PROFILE_IMG_URL AS PROFILE_IMG_URL, A.STATUS, A.THUMBNAIL_URL, A.TAG, A.IS_NOTICE, (SELECT COUNT(SEQ) FROM IMB_COMMENT WHERE A.BOARD_ID = BOARD_ID AND A.SEQ = ARTICLE_SEQ) AS COMMENT_COUNT, A.PASSWORD, C.STATUS AS READ_STATUS FROM IMB_ARTICLE AS A LEFT OUTER JOIN IMB_USER B ON A.WRITER_ID = B.ID LEFT OUTER JOIN IMB_ARTICLE_READER C ON C.BOARD_ID = A.BOARD_ID AND C.ARTICLE_SEQ = A.SEQ AND C.USER_ID = #{signinUserId} LEFT OUTER JOIN IMB_BOARD_AUTH D ON A.BOARD_ID = D.BOARD_ID WHERE (IS_NOTICE IS NULL OR IS_NOTICE = 'N') AND (D.VIEW_LIST_LEVEL IS NULL OR D.VIEW_LIST_LEVEL >= #{signinUserLevel}) AND (A.STATUS = 1 OR A.WRITER_ID = #{signinUserId} OR #{signinUserLevel} < 0)
AND A.BOARD_ID = #{boardId}
AND SUBJECT LIKE CONCAT('%',#{subject},'%')
AND CONTENT LIKE CONCAT('%',#{content},'%')
AND WRITER_ID = #{writerId}
AND WRITER_ID LIKE CONCAT('%',#{likeWriterId},'%')
AND WRITER_NAME = #{writerName}
AND WRITER_NAME LIKE CONCAT('%',#{likeWriterName},'%')
AND TAG LIKE CONCAT('%', #{tag}, '%')
AND A.STATUS = #{status}
AND C.STATUS = #{readStatus}
AND B.DISPLAY_ID LIKE CONCAT('%', #{likeWriterDisplayId}, '%')
AND B.DISPLAY_ID = #{writerDisplayId}
AND DATE_FORMAT(REGISTER_DATE, '%Y-%m-%d') = #{registerDate}
AND THUMBNAIL_URL IS NOT NULL AND THUMBNAIL_URL != ''
)
) ARTICLE ORDER BY IS_NOTICE DESC, REGISTER_DATE DESC , GROUP_ID DESC , SEQ DESC
LIMIT #{startIndex}, #{endIndex}

SELECT A.BOARD_ID, (SELECT NAME FROM IMB_BOARD WHERE ID = A.BOARD_ID) AS BOARD_NAME, SEQ, SUBJECT, CONTENT, DATE_FORMAT(A.REGISTER_DATE, '%Y-%m-%d %H:%i:%s') AS REGISTER_DATE, HIT, GOOD, BAD, A.WRITER_ID, B.DISPLAY_ID AS WRITER_DISPLAY_ID, GROUP_ID, A.WRITER_NAME, STATUS, THUMBNAIL_URL, TAG, IS_NOTICE, (SELECT COUNT(SEQ) FROM IMB_COMMENT WHERE A.BOARD_ID = BOARD_ID AND A.SEQ = ARTICLE_SEQ) AS COMMENT_COUNT, A.PASSWORD, (SELECT STATUS FROM IMB_ARTICLE_READER WHERE BOARD_ID = A.BOARD_ID AND ARTICLE_SEQ = A.SEQ AND USER_ID = #{signinUserId}) AS READ_STATUS FROM IMB_ARTICLE AS A LEFT OUTER JOIN IMB_USER B ON A.WRITER_ID = B.ID LEFT OUTER JOIN IMB_BOARD_AUTH D ON D.BOARD_ID = A.BOARD_ID WHERE (D.VIEW_DETAIL_LEVEL IS NULL OR D.VIEW_DETAIL_LEVEL >= #{signinUserLevel}) AND (A.STATUS = 1 OR A.WRITER_ID = #{signinUserId} OR #{signinUserLevel} < 0)
AND A.BOARD_ID = #{boardId}
AND SEQ = #{seq}
AND SUBJECT LIKE CONCAT('%',#{subject},'%')
AND CONTENT LIKE CONCAT('%',#{content},'%')
AND WRITER_ID = #{writerId}
AND WRITER_ID LIKE CONCAT('%',#{likeWriterId},'%')
AND WRITER_NAME = #{writerName}
AND WRITER_NAME LIKE CONCAT('%',#{likeWriterName},'%')
AND TAG LIKE CONCAT('%', #{tag}, '%')
AND A.STATUS = #{status}
AND C.STATUS = #{readStatus}
AND B.DISPLAY_ID LIKE CONCAT('%', #{likeWriterDisplayId}, '%')
AND B.DISPLAY_ID = #{writerDisplayId}
AND IS_NOTICE = #{isNotice}
AND DATE_FORMAT(REGISTER_DATE, '%Y-%m-%d') = #{registerDate}
AND THUMBNAIL_URL IS NOT NULL AND THUMBNAIL_URL != ''

mapper.insert:
INSERT INTO IMB_ARTICLE (BOARD_ID, SEQ, SUBJECT, CONTENT, WRITER_ID, WRITER_NAME, GROUP_ID, PARENT_SEQ, THUMBNAIL_URL, TAG, PASSWORD, IS_NOTICE, STATUS) VALUES ( #{boardId}, #{seq}, #{subject}, #{content}, #{writerId}, (SELECT NAME FROM IMB_USER WHERE ID = #{writerId}),
#{groupId}
#{seq}
, #{parentSeq}, #{thumbnailUrl}, #{tag}, #{password}, #{isNotice},
#{status}
1
)

mapper.update:
UPDATE IMB_ARTICLE SET
	HIT = HIT + 1
WHERE
		BOARD_ID = #{boardId}
	AND SEQ = #{seq}

UPDATE IMB_ARTICLE SET
	GOOD = GOOD + 1
WHERE
		BOARD_ID = #{boardId}
	AND SEQ = #{seq}

UPDATE IMB_ARTICLE SET
	BAD = BAD + 1
WHERE
		BOARD_ID = #{boardId}
	AND SEQ = #{seq}

UPDATE IMB_ARTICLE SET
	STATUS = #{status}
WHERE
		BOARD_ID = #{boardId}
	AND SEQ = #{seq}

mapper.delete:
DELETE FROM
	IMB_ARTICLE
WHERE
		BOARD_ID = #{boardId}
	AND SEQ = #{seq}
