mapper.update:
	UPDATE
		IMB_ARTICLE_READER
	SET
		STATUS = #{status}
	WHERE
   		BOARD_ID = #{boardId}
   	AND ARTICLE_SEQ = #{articleSeq}
   	AND USER_ID = #{userId}

mapper.delete:
DELETE FROM
	IMB_ARTICLE_READER
WHERE
		BOARD_ID = #{boardId}
	AND ARTICLE_SEQ = #{articleSeq}
	AND USER_ID = #{userId}
