mapper.select:
 SELECT
 	COUNT(IMBOARD_ID) AS COUNT
 FROM
 	IMB_COMMENT
 WHERE
 		BOARD_ID = #{boardId}
 	AND ARTICLE_SEQ = #{articleSeq}

     	AND CONTENT LIKE CONCAT('%',#{content},'%')


 		AND A.WRITER_ID = #{writerId}


 		AND A.WRITER_ID LIKE CONCAT('%',#{likeWriterId},'%')


 		AND A.WRITER_NAME = #{writerName}


 		AND A.WRITER_NAME LIKE CONCAT('%',#{likeWriterName},'%')


 		AND B.DISPLAY_ID = #{writerDisplayId}


 		AND B.DISPLAY_ID LIKE CONCAT('%',#{likeWriterDisplayId},'%')

 ORDER BY
 	GROUP_ID ${orderByGroupId}ASC
		,SEQ ${orderBySeq}ASC
		, REGISTER_DATE ${orderByRegisterDate}


 		LIMIT #{startIndex}, #{endIndex}

SELECT A.BOARD_ID, A.ARTICLE_SEQ, A.SEQ, A.CONTENT,
( CASE WHEN DATE_FORMAT(now(), '%Y-%m-%d') = DATE_FORMAT(A.REGISTER_DATE, '%Y-%m-%d') THEN DATE_FORMAT(A.REGISTER_DATE, '%H:%i:%s') ELSE DATE_FORMAT(A.REGISTER_DATE, '%Y-%m-%d') END ) AS REGISTER_DATE,
DATE_FORMAT(A.REGISTER_DATE, '%Y-%m-%d %H:%i:%s') AS REGISTER_DATE,
A.GOOD, A.BAD, A.WRITER_ID, CASE WHEN B.NAME IS NULL THEN A.WRITER_NAME ELSE B.NAME END AS WRITER_NAME, B.DISPLAY_ID AS WRITER_DISPLAY_ID, A.PARENT_WRITER_ID, C.ID AS PARENT_WRITER_ID, CASE WHEN C.NAME IS NULL THEN A.PARENT_WRITER_NAME ELSE C.NAME END AS PARENT_WRITER_NAME, C.DISPLAY_ID AS PARENT_WRITER_DISPLAY_ID, B.PROFILE_IMG_URL AS WRITER_PROFILE_IMG_URL, A.PARENT_SEQ, A.GROUP_ID, A.STATUS FROM IMB_COMMENT AS A LEFT OUTER JOIN IMB_USER B ON A.WRITER_ID = B.ID LEFT OUTER JOIN IMB_USER C ON A.PARENT_WRITER_ID = C.ID WHERE A.BOARD_ID = #{boardId} AND A.ARTICLE_SEQ = #{articleSeq}
AND CONTENT LIKE CONCAT('%',#{content},'%')
AND A.WRITER_ID = #{writerId}
AND A.WRITER_ID LIKE CONCAT('%',#{likeWriterId},'%')
AND A.WRITER_NAME = #{writerName}
AND A.WRITER_NAME LIKE CONCAT('%',#{likeWriterName},'%')
AND B.DISPLAY_ID = #{writerDisplayId}
AND B.DISPLAY_ID LIKE CONCAT('%',#{likeWriterDisplayId},'%')
ORDER BY GROUP_ID ${orderByGroupId}ASC ,SEQ ${orderBySeq}ASC , REGISTER_DATE ${orderByRegisterDate}
LIMIT #{startIndex}, #{endIndex}

mapper.insert:
INSERT INTO IMB_COMMENT (BOARD_ID, ARTICLE_SEQ, SEQ, CONTENT, WRITER_ID, WRITER_NAME, GROUP_ID, PARENT_SEQ, PARENT_WRITER_ID, PARENT_WRITER_NAME) VALUES ( #{boardId}, #{articleSeq}, #{seq}, #{content}, #{writerId}, #{writerName},
#{groupId}
#{seq}
, #{parentSeq}, #{parentWriterId}, #{parentWriterName} )

mapper.update:
UPDATE IMB_COMMENT SET
	CONTENT = #{content},
	REGISTER_DATE = CURRENT_TIMESTAMP
WHERE
		BOARD_ID = #{boardId}
	AND ARTICLE_SEQ = #{articleSeq}
	AND SEQ = #{seq}

UPDATE IMB_COMMENT SET
	GOOD = GOOD + 1
WHERE
		BOARD_ID = #{boardId}
	AND ARTICLE_SEQ = #{articleSeq}
	AND SEQ = #{seq}

UPDATE IMB_COMMENT SET
	BAD = BAD + 1
WHERE
		BOARD_ID = #{boardId}
	AND ARTICLE_SEQ = #{articleSeq}
	AND SEQ = #{seq}

UPDATE IMB_COMMENT SET
	STATUS = #{status}
WHERE
		BOARD_ID = #{boardId}
	AND ARTICLE_SEQ = #{articleSeq}
	AND SEQ = #{seq}

mapper.delete:
DELETE FROM
	IMB_COMMENT
WHERE
		BOARD_ID = #{boardId}
	AND ARTICLE_SEQ = #{articleSeq}
	AND SEQ = #{seq}

DELETE FROM
	IMB_COMMENT
WHERE
		BOARD_ID = #{boardId}
	AND ARTICLE_SEQ = #{articleSeq}
