mapper.select:
SELECT ID, NAME, TYPE, PARAMETER, PRIORITY, PARENT_MENU_ID, VIEW_LEVEL, (SELECT COUNT(*) FROM IMB_MENU WHERE PARENT_MENU_ID = A.ID) AS CHILD_MENU_COUNT, CREATOR FROM IMB_MENU A WHERE
PARENT_MENU_ID IS NULL OR PARENT_MENU_ID = ID PARENT_MENU_ID = #{parentMenuId}
ORDER BY PARENT_MENU_ID, PRIORITY

SELECT
	ID,
	NAME,
	TYPE,
	PARAMETER,
	PRIORITY,
	PARENT_MENU_ID,
	VIEW_LEVEL,
	(SELECT COUNT(*) FROM IMB_MENU WHERE PARENT_MENU_ID = A.ID) AS CHILD_MENU_COUNT,
	CREATOR
FROM
	IMB_MENU A
WHERE
	1 = 1
	
		AND ID = #{id}
	
	
		AND NAME = #{name}
	
	
		AND TYPE = #{type}
	
	
		AND PARAMETER = #{parameter}
	
	
		AND PARENT_MENU_ID = #{parentMenuId}
	
	
		AND CREATOR = #{creator}

mapper.update:
 UPDATE
 	IMB_MENU
	SET
		NAME = #{name}
		TYPE = #{type}
		,PARAMETER = #{parameter}
		,PRIORITY = #{priority}
		,PARENT_MENU_ID = #{parentMenuId}
		,VIEW_LEVEL = #{viewLevel}
 WHERE
		ID = #{id}
